package main

import (
	"encoding/json"
	"fmt"
	"github.com/gorilla/websocket"
	"net/http"
	"os"
)

type PingResponse struct {
	MachineName string `json:"machineName"`
}

type Method struct {
	Method string `json:"Method"`
}

type Connected struct {
	// "Method": "CONNECTED",
	Method string `json:"Method"`
	// "Client-Id": "q9pttrzqq",
	ClientId string `json:"Client-Id"`
	// "API": 20,
	API int `json:"API"`
	// "Device-Type": "Web"
	DeviceType string `json:"Device-Type"`
}

type GetConfig struct {
	// "Method": "GET_CONFIG",
	Method string `json:"Method"`
	// "Rows": 3,
	Rows int `json:"Rows"`
	// "Columns": 5,
	Columns int `json:"Columns"`
	// "ButtonSpacing": 10,
	ButtonSpacing int `json:"ButtonSpacing"`
	// "ButtonRadius": 40,
	ButtonRadius int `json:"ButtonRadius"`
	// "ButtonBackground": true,
	ButtonBackground bool `json:"ButtonBackground"`
	// "Brightness": 0.3,
	Brightness float32 `json:"Brightness"`
	// "AutoConnect": false,
	AutoConnect bool `json:"AutoConnect"`
	// "WakeLock": "Connected",
	WakeLock string `json:"WakeLock"`
	// "SupportButtonReleaseLongPress": true
	SupportButtonReleaseLongPress bool `json:"SupportButtonReleaseLongPress"`
}

type Button struct {
	// "IconBase64": "",
	IconBase64 string `json:"IconBase64"`
	// "Position_X": 0,
	PositionX int `json:"Position_X"`
	// "Position_Y": 0,
	PositionY int `json:"Position_Y"`
	// "LabelBase64": "",
	LabelBase64 string `json:"LabelBase64"`
	// "BackgroundColorHex": "#232323"
	BackgroundColorHex string `json:"BackgroundColorHex"`
}

type Buttons struct {
	// "Method": "GET_BUTTONS",
	Method string `json:"Method"`
	// "Buttons": []
	Buttons []Button `json:"Buttons"`
}

var upgrader = websocket.Upgrader{
	CheckOrigin: func(r *http.Request) bool {
		return true // Allow all connections (for development purposes)
	},
}

// WebSocket handler
func wsHandler(w http.ResponseWriter, r *http.Request) {
	// Upgrade the HTTP connection to a WebSocket connection
	conn, err := upgrader.Upgrade(w, r, nil)
	if err != nil {
		fmt.Println("Error while upgrading connection:", err)
		return
	}
	defer conn.Close()
	fmt.Println("Client connected")
	// Echo messages back to the client
	for {
		// Read message from the client
		messageType, msg, err := conn.ReadMessage()
		if err != nil {
			fmt.Println("Error while reading message:", err)
			break
		}

		var method Method
		json.Unmarshal(msg, &method)

		switch method.Method {
		case "BUTTON_LONG_PRESS":
		case "BUTTON_LONG_PRESS_RELEASE":
		case "BUTTON_PRESS":
		case "BUTTON_RELEASE":
			update_buttons := Buttons{
				Method: "UPDATE_BUTTON",
				Buttons: []Button{
					{
						IconBase64:         "",
						PositionX:          0,
						PositionY:          0,
						LabelBase64:        "",
						BackgroundColorHex: "#232323",
					},
				},
			}
			buttons_response, err := json.Marshal(update_buttons)
			if err != nil {
				break
			}
			conn.WriteMessage(messageType, buttons_response)
		case "GET_BUTTONS":
			get_buttons := Buttons{
				Method: "GET_BUTTONS",
				Buttons: []Button{
					{
						IconBase64:         "",
						PositionX:          1,
						PositionY:          0,
						LabelBase64:        "",
						BackgroundColorHex: "#232323",
					},
					{
						IconBase64:         "",
						PositionX:          0,
						PositionY:          0,
						LabelBase64:        "",
						BackgroundColorHex: "#232323",
					},
				},
			}
			buttons_response, err := json.Marshal(get_buttons)
			if err != nil {
				break
			}
			conn.WriteMessage(messageType, buttons_response)
		case "CONNECTED":
			var connected Connected
			json.Unmarshal(msg, &connected)

			get_config := GetConfig{
				Method:                        "GET_CONFIG",
				Rows:                          4,
				Columns:                       6,
				ButtonSpacing:                 10,
				ButtonRadius:                  40,
				ButtonBackground:              true,
				Brightness:                    0.3,
				AutoConnect:                   false,
				WakeLock:                      "Connected",
				SupportButtonReleaseLongPress: true,
			}

			config_response, err := json.Marshal(get_config)
			if err != nil {
				break
			}
			conn.WriteMessage(messageType, config_response)

			get_buttons := Buttons{
				Method: "GET_BUTTONS",
				Buttons: []Button{
					{
						IconBase64:         "",
						PositionX:          1,
						PositionY:          0,
						LabelBase64:        "",
						BackgroundColorHex: "#232323",
					},
					{
						IconBase64:         "",
						PositionX:          0,
						PositionY:          0,
						LabelBase64:        "",
						BackgroundColorHex: "#232323",
					},
				},
			}
			buttons_response, err := json.Marshal(get_buttons)
			if err != nil {
				break
			}
			conn.WriteMessage(messageType, buttons_response)
		}

		// Print the received message
		fmt.Printf("Received: %s\n", msg)
	}
}

// pingHandler handles the /ping route
func pingHandler(w http.ResponseWriter, r *http.Request) {
	// Get the hostname
	hostname, err := os.Hostname()
	if err != nil {
		http.Error(w, "Unable to get hostname", http.StatusInternalServerError)
		return
	}
	// Create the response object
	response := PingResponse{
		MachineName: hostname,
	}
	// Set the content type to application/json
	w.Header().Set("Content-Type", "application/json")
	// Encode the response as JSON and write it to the response writer
	if err := json.NewEncoder(w).Encode(response); err != nil {
		http.Error(w, "Error encoding JSON", http.StatusInternalServerError)
	}
}

func main() {
	fmt.Println("Hello, World!")
	http.HandleFunc("/", wsHandler)
	http.HandleFunc("/ping", pingHandler)
	// Start the HTTP server on port 8191
	fmt.Println("Starting server on :8191")
	if err := http.ListenAndServe(":8191", nil); err != nil {
		fmt.Println("Error starting server:", err)
	}
}
